mapper.select:
select p.* from point p
LEFT JOIN point_info pi on p.code=pi.CODE
where  p.status=1

and p.code=#{code,jdbcType=VARCHAR}


and trans_code=#{transCode,jdbcType=VARCHAR} 


and p.type=#{type,jdbcType=VARCHAR}


and bom_code = #{bomCode,jdbcType=VARCHAR}


and pi.name like CONCAT('%',#{name},'%')

select p.* from point p inner join point_info pi on p.code=pi.code where status = 1 

	and p.code = #{code,jdbcType=VARCHAR}


	and object_code = #{equipmentCode,jdbcType=VARCHAR}


	and name like CONCAT('%',#{name},'%')


	and bom_code = #{bomCode,jdbcType=VARCHAR}

limit 0,1

select (select group_concat(p.code) from equipment e join equipment_info ei on e.code=ei.code and e.status=1
and ei.name like concat('%',#{name,jdbcType=VARCHAR})
and e.category_code=#{categoryCode,jdbcType=VARCHAR}
and e.trans_code=#{transCode,jdbcType=VARCHAR}
join point p on p.object_code = e.code and p.status = 1 join point_info pi on pi.code = p.code and pi.name like concat('%',#{model,jdbcType=VARCHAR},'%')) pointCode from dual

select p.code,(select name from equipment_bom_info where code=p.bom_code limit 1) ename,
(select name from point_info pi where pi.code=p.code limit 1) pname,SUBSTRING_INDEX(p.config,'|',1) unit 
from point p  where trans_code=#{transCode} and p.status=1 limit #{index},#{size}

select p.code from point p join point_info pi on p.code=pi.code where 1=1
and p.trans_code=#{_paramter,jdbcType=VARCHAR}
and pi.name like '环境%温度%' ORDER BY p.create_time desc limit 1

select tp.point_code as code,tp.name as name,ei.code equipmentCode,ei.name equipmentName from trans_runcondition_point tp LEFT JOIN point p on p.code=tp.point_code LEFT JOIN equipment_info ei on ei.code=p.object_code where tp.status=1 and tp.type=1
and tp.trans_code=#{transCode,jdbcType=VARCHAR}
ORDER BY ei.name,orders

select tp.point_code as code,tp.name as name,ei.code equipmentCode,ei.name equipmentName from trans_runcondition_point tp LEFT JOIN point p on p.code=tp.point_code LEFT JOIN equipment_info ei on ei.code=p.object_code where tp.status=1 and tp.type=2
and tp.trans_code=#{transCode,jdbcType=VARCHAR}
ORDER BY ei.name,orders

select tp.point_code as code,tp.name as name,ei.code equipmentCode,ei.name equipmentName from trans_runcondition_point tp LEFT JOIN point p on p.code=tp.point_code LEFT JOIN equipment_info ei on ei.code=p.object_code where tp.status=1 and tp.type=3
and tp.trans_code=#{transCode,jdbcType=VARCHAR}
ORDER BY ei.name,orders

select tp.point_code as code,tp.name as name,ei.code equipmentCode,ei.name equipmentName from trans_runcondition_point tp LEFT JOIN point p on p.code=tp.point_code LEFT JOIN equipment_info ei on ei.code=p.object_code where tp.status=1 and tp.type=4
and tp.trans_code=#{transCode,jdbcType=VARCHAR}
ORDER BY ei.name,orders

select point_code code,name ,type from trans_runcondition_point where status=1

and trans_code=#{transCode,jdbcType=VARCHAR}


and type=#{type,jdbcType=VARCHAR}

 limit 5
